INSERT INTO `mod121_posts` VALUES (6131,2,'2020-10-30 15:29:02','2020-10-30 13:29:02','Bon à savoir :\r\n\r\n<strong>Les mesures barrières sont mises en place :</strong>\r\n<strong>- désinfection et aération du cabinet après chaque patient</strong>\r\n<strong>- gel hydro alcoolique mis a disposition</strong>\r\n<strong>- port du masque obligatoire</strong>\r\n<strong>- distance de sécurité respectées\r\n</strong>\r\n\r\nDes<strong> téléconsultations</strong> sont également possibles.\r\n\r\n<strong>Pour plus d\'informations ou pour une prise de rendez-vous, contactez-moi directement sur le site ou bien au 06 65 54 17 93.</strong>\r\n\r\nA bientôt.','Le cabinet est ouvert de 11h à 21h du lundi au vendredi','','trash','closed','closed','','le-cabinet-est-ouvert-de-11h-a-21h-du-lundi-au-vendredi','','','2025-02-27 15:18:09','2025-02-27 13:18:09','',0,'https://www.hypnose-sophrologie-nantes.com/?post_type=popup&#038;p=6131',0,'popup','',0);
