INSERT INTO `mod121_posts` VALUES (6273,2,'2021-12-09 13:42:10','2021-12-09 11:42:10','La <strong>sophrologie Caycédienne</strong> est un mélange de techniques de <strong>yoga</strong> et d’<strong>hypnose</strong>. Cette thérapie brève permet de retrouver le<strong> contrôle de vos émotions</strong> et d’harmoniser les messages du corps avec votre esprit.\r\n\r\nLa <strong>sophrologie</strong> permet de retrouver un équilibre psychologique et comportemental en utilisant trois techniques fondamentales:\r\n- la <strong>respiration</strong> contrôlée\r\n- la détente musculaire\r\n- la <strong>visualisation</strong> positive\r\n\r\nJe vous construirai un protocole personnalisé suite à une première séance de découverte ou nous échangerons sur vos problématiques et trouverons ensemble des objectifs précis à atteindre.\r\n\r\nJ’utilise la <strong>Sophrologie</strong> pour les personnes ayant besoin de retrouver le contrôle de leurs émotions (stress, angoisse, peur, crise d’angoisse, colère, …), de se connecter à leur corps afin de mieux le comprendre et d’y évacuer des tensions.\r\n\r\nCette technique est donc adaptable à beaucoup de domaine : la prise de parole, le burn-out, le sport, la scolarité, le sommeil, les peurs et phobies, l’entretien corporel …\r\n\r\nRetrouvez-moi au 10 rue Fredureau à <strong>Nantes</strong> pour votre séance.','La Sophrologie en quelques mots','','publish','closed','closed','','la-sophrologie','','','2021-12-09 13:48:02','2021-12-09 11:48:02','',0,'https://www.hypnose-sophrologie-nantes.com/?p=6273',0,'post','',0);
INSERT INTO `mod121_posts` VALUES (6274,2,'2021-12-09 13:42:10','2021-12-09 11:42:10','La <strong>sophrologie Caycédienne</strong> est un mélange de techniques de <strong>yoga</strong> et d’<strong>hypnose</strong>. Cette thérapie brève permet de retrouver le<strong> contrôle de vos émotions</strong> et d’harmoniser les messages du corps avec votre esprit.\r\n\r\nLa <strong>sophrologie</strong> permet de retrouver un équilibre psychologique et comportemental en utilisant trois techniques fondamentales:\r\n- la <strong>respiration</strong> contrôlée\r\n- la détente musculaire\r\n- la <strong>visualisation</strong> positive\r\n\r\nJe vous construirai un protocole personnalisé suite à une première séance de découverte ou nous échangerons sur vos problématiques et trouverons ensemble des objectifs précis à atteindre.\r\n\r\nJ’utilise la <strong>Sophrologie</strong> pour les personnes ayant besoin de retrouver le contrôle de leurs émotions (stress, angoisse, peur, crise d’angoisse, colère, …), de se connecter à leur corps afin de mieux le comprendre et d’y évacuer des tensions.\r\n\r\nCette technique est donc adaptable à beaucoup de domaine : la prise de parole, le burn-out, le sport, la scolarité, le sommeil, les peurs et phobies, l’entretien corporel …\r\n\r\nRetrouvez-moi au 10 rue Fredureau à <strong>Nantes</strong> pour votre séance.','La Sophrologie en quelques mots','','inherit','closed','closed','','6273-revision-v1','','','2021-12-09 13:42:10','2021-12-09 11:42:10','',6273,'https://www.hypnose-sophrologie-nantes.com/?p=6274',0,'revision','',0);
INSERT INTO `mod121_posts` VALUES (6393,2,'2022-09-08 09:20:56','2022-09-08 07:20:56','[vc_row][vc_column width=\"2/3\"][mk_custom_list]<span style=\"font-weight: 400;\"><strong>L’addiction</strong> est un vaste sujet qui peut toucher beaucoup de domaines : </span>\r\n<ul>\r\n 	<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">Alimentaire (sucre, sel, grignotage,…)</span></li>\r\n 	<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">Produits (tabac, marijuana, alcool,…) </span></li>\r\n 	<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">Sexuelle</span></li>\r\n 	<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">Relationnelle (certaine personne développent une dépendance émotionnelle envers une autre personne</span></li>\r\n 	<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">Sport (performance,...)</span></li>\r\n 	<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">Écrans (jeux vidéos, portable,…)</span></li>\r\n 	<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">Jeux (casino, jeux en ligne, …)</span></li>\r\n</ul>\r\n[/mk_custom_list][/vc_column][vc_column width=\"1/3\"][vc_single_image image=\"5446\" img_size=\"full\"][/vc_column][/vc_row][vc_row][vc_column][mk_custom_list]<span style=\"font-weight: 400;\">Il est nécessaire :</span>\r\n<ul>\r\n 	<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">De <strong>comprendre le processus</strong> qui s’est mis en place (quelle situation génère <strong>l’addiction </strong>? quel est le rythme de cette <strong>addiction </strong>? …)</span></li>\r\n 	<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">d’être attentif aux réactions corporelles lorsque vous êtes face à votre <strong>addiction</strong></span></li>\r\n 	<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">de remonter dans le temps afin de trouver un élément déclencheur (s’il y en a un)</span></li>\r\n</ul>\r\n<span style=\"font-weight: 400;\">Je vous aiderai à répondre à ces questions et vous proposerai un protocole adapté pour atteindre vos objectifs et vous apaiser en vous permettant de retrouver le contrôle.</span>\r\n\r\n<span style=\"font-weight: 400;\">Il est possible que je vous propose :</span>\r\n<ul>\r\n 	<li style=\"font-weight: 400;\"><strong>des techniques de respiration</strong></li>\r\n 	<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">des techniques de relaxation dynamique</span></li>\r\n 	<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">des visualisations</span></li>\r\n 	<li style=\"font-weight: 400;\"><span style=\"font-weight: 400;\">de <strong>l’hypnose</strong></span></li>\r\n</ul>\r\n[/mk_custom_list][/vc_column][/vc_row][vc_row][vc_column][vc_column_text][/vc_column_text][mk_fancy_title tag_name=\"h3\" color=\"#dd9933\" font_family=\"none\" align=\"center\" animation=\"bottom-to-top\"]Je vous accueille dans mon <strong>cabinet d’hypnose et de sophrologie au 20bis boulevard de Launay 44100 Nantes.\r\n</strong>\r\n\r\nRetrouver le <strong>contrôle sur ses addictions</strong> nécessite un véritable processus sophrologique et hypnotique de plusieurs séances, chaque séance ayant un objectif précis.\r\n<strong>Il s’agit d’une thérapie brève. </strong>[/mk_fancy_title][vc_btn title=\"Prendre rendez-vous\" color=\"sandy-brown\" size=\"lg\" align=\"center\" i_icon_fontawesome=\"fa fa-comments-o\" css_animation=\"left-to-right\" link=\"url:%2Fcontact||\" add_icon=\"true\"][/vc_column][/vc_row]','Addiction','','inherit','closed','closed','','5445-revision-v1','','','2022-09-08 09:20:56','2022-09-08 07:20:56','',5445,'https://www.hypnose-sophrologie-nantes.com/?p=6393',0,'revision','',0);
